import React from 'react';
import { FeedbackInterface } from '../data/FeedbackData';
import { OrNull } from '../utils';

export interface FeedbackStatsInterface {
    feedbacks: OrNull<Map<number, FeedbackInterface>>,
};

const FeedbackStats = (props : FeedbackStatsInterface) => {
    let average : number = ! props.feedbacks ? 0 : [...props.feedbacks.values()].reduce ((acc, curr) => {
        return acc + curr.rating;
    }, 0) / props.feedbacks.size;
  return (
    <div className='feedback-stats'>
          <h4>{props.feedbacks?.size} Reviews</h4>
          <h4>Average rating: {average}</h4>
        </div>
  )
};

export default FeedbackStats
