import FeedbackItem from "./components/FeedbackItem";
import FeedbackList, { FeedbackListInterface } from "./components/FeedbackList";
import Header from "./components/Header";
import {  feedbackDataMap, FeedbackInterface } from './data/FeedbackData'
import {useState} from 'react';
import { OrNull } from "./utils";
import FeedbackStats from "./components/FeedbackStats";
export interface AppModel  { 
    message: string,
    feedBacks: FeedbackListInterface 
}




function App () {

    const [feedback, setFeedback] = useState<OrNull<Map<number, FeedbackInterface>>>(feedbackDataMap);
    const deleteFeedback = (id : number) : void => {
        if (window.confirm(`are you sure you want to delete the item :  ${JSON.stringify(feedback?.get(id))} ?`)) {
            setFeedback(fb => {
                const fbc = fb;
                fbc?.delete(id);
                return (fbc); 
            });
            
        }
        
    } 

    return (
        <>
        <Header />
        <div className="container">
                <FeedbackStats feedbacks={feedback} />
                <FeedbackList feedbacks={!feedback ? [] : []} handleDelete={deleteFeedback} />
        </div>
        </>
        
    )
}

export default App;
