import React from 'react';
import {useContext} from 'react';
import {motion, AnimatePresence} from 'framer-motion';
import { FeedbackInterface } from '../data/FeedbackData'
import { OrNull } from '../utils';
import FeedbackItem from './FeedbackItem'
import { FeedbackContext } from './context/FeedbackContext';
export interface FeedbackListInterface {
    feedbacks: Array<FeedbackInterface>, // OrNull<Map<number, FeedbackInterface>>,
    handleDelete: (id : string) => void;
}

const FeedbackList = ({ handleDelete} : FeedbackListInterface) => {
  const { feedbacks } = useContext(FeedbackContext);
    if (!feedbacks || !feedbacks || feedbacks.size === 0) {
    return <p>no feedback yet</p>
  }
   const vals = [...feedbacks.values()];
  return (<div className="feedback-list">
    <AnimatePresence>
      {
        vals.map((item) => (
          <motion.div key={item.id} initial={{
            opacity: 0
          }} 
          animate={{opacity: 1}}
          exit={{opacity: 1}}
          
          >
            <FeedbackItem key={item.id}
              handleDelete={handleDelete}
              text={item.text} rating={item.rating} id={item.id} />
          </motion.div>
          
        ))
      }
    </AnimatePresence>
    
  </div>);
 /*  return (<div className="feedback-list">
    {
        
          feedbacks.map((item) => (   
            <FeedbackItem key={item.id} 
            handleDelete = {handleDelete}
            text={item.text} rating={item.rating} id={item.id} />
        ))
    }
  </div>); */
}

export default FeedbackList
