import { createContext, ReactNode, useState } from 'react';
import { mapDeepCopy, OrNull } from '../../utils';
import { FeedbackInterface } from '../FeedbackForm';


export interface FeedbackProviderModelInterface  {
    children ? : ReactNode,
    feedbacks?: OrNull<Map<string, FeedbackInterface>>,
    deleteFeedback: (id : string) => void,
}
export const FeedbackContext = createContext<FeedbackProviderModelInterface>({feedbacks: null});
export const FeedbackProvider = ({children} : FeedbackProviderModelInterface) =>{
    const [feedback, setFeedback] = useState<OrNull<Map<string, FeedbackInterface>>>(null);

    const deleteFeedback = (id: string): void => {
        if (window.confirm(`are you sure you want to delete the item :  ${JSON.stringify(feedback?.get(id))} ?`)) {
            const copy = mapDeepCopy(feedback);
            if (copy?.delete(id)) {
                setFeedback(copy);
            }
            // setFeedback(fb => {
            //     const fbc = fb;
            //     fbc?.delete(id);
            //     return (fbc); 
            // });

        }

    }

    return <FeedbackContext.Provider value={{
        feedbacks: feedback,
        deleteFeedback: deleteFeedback
    }}>
        {children}
    </FeedbackContext.Provider>
}
