import React from 'react';
import {useState} from 'react';
import { setFlagsFromString } from 'v8';

export interface FeedbackItemModel {
    rating: number,
    text: string,
}

function FeedbackItem() {
    const [fedback, setFeedback] = useState<FeedbackItemModel>({rating: 6, text: 'this is an example of a feedback item'});
    const handleClick = () => {
        setFeedback((prev) => {
            let pr = prev;
            pr.rating = pr.rating + 4;
            return pr;
        })
    }
  return (
    <div className='card'>
        <div className="num-display">{ fedback.rating }</div>
        <div className="text-display">{fedback.text}</div>
        <button>Click</button>
    </div>
  )
}

export default FeedbackItem
